import './index_body.css';

import React, { useEffect } from 'react';
import Particles from 'react-particles-js';

const Index_body = () =>{
	return(
		<React.Fragment>
			<div className="index_body">
				<Particles
    				params={{
    					particles: {
    						number: {
    							value: 100,

	        				},
    						line_linked: {
            					shadow: {
            						enable: true,
            						color: "white",
            						blur: 5,
            					}
            				}
            			},
            			"interactivity": {
            				detect_on:"canvas",
            				events:{
	            				onhover: {
	            					enable: true,
	            					modes: "grab",
	            				},
	            				onclick:{
									enable: true,
									mode: "push",
								},

							},
							modes:{
								grab:{
									distance:200,
									line_linked:{
										opacity:1
									}
								},
							}
            			}
					}}
				/>
			</div>
		</React.Fragment>
	);
}

export default Index_body;
