import './index_body.css';

import React, { useEffect } from 'react';
import Particles from 'react-particles-js';

const Index_body = () =>{
	return(
		<React.Fragment>
			<div className="index_body">
				<Particles
    				params={{
    					particles: {
    						number: {
    							value: 80,
    							density:{
                  					enable: true,
                 		 			value_area: 800
               					}	
	        				},
    						line_linked: {
            					shadow: {
            						enable: true,
            						color: "white",
            						blur: 5,
            					}
            				},
            				move:{
				                enable:true,
				                speed:6,
				                direction: "none",
				                random: false,
				                straight: false,
				                out_mode: "out",
				                bounce: false,
				                attract:{
				                  enable: false,
				                  rotateX: 600,
				                  rotateY: 1200
				              	}
				            }
            			},
            			interactivity: {
            				detect_on:"canvas",
            				events:{
	            				onhover: {
	            					enable: true,
	            					modes: "grab",
	            				},
	            				onclick:{
									enable: true,
									mode: "push",
								},

							},
							modes:{
								grab:{
									distance:200,
									line_linked:{
										opacity:1
									}
								},
								push:{
									particles_nb:4
								},
							}
            			}
					}}
				/>
			</div>
		</React.Fragment>
	);
}

export default Index_body;
