import React from 'react';
import styled from 'styled-components';
import Burger from './Burger';

const NavMobile = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 22;
  height: 84px;
  padding: 0 24px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: var(--nav-bg);
  border-bottom: 1px solid var(--nav-line);
  backdrop-filter: blur(10px);

  .logo {
    display: flex;
    align-items: center;
  }

  .logo img {
    width: 50px;
    height: 50px;
    border-radius: 16px;
    border: 1px solid var(--line);
    box-shadow: var(--shadow-soft);
    background: var(--surface-strong);
  }

  .nav-actions {
    display: flex;
    align-items: center;
    gap: 0.65rem;
  }

  .theme-toggle {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-width: 78px;
    height: 38px;
    border-radius: 999px;
    border: 1px solid var(--line);
    background: var(--nav-pill-bg);
    color: var(--text);
    font-family: 'Space Grotesk', sans-serif;
    font-size: 0.8rem;
    font-weight: 700;
    cursor: pointer;
    transition: transform 0.2s ease, box-shadow 0.2s ease, border-color 0.2s ease;
  }

  .theme-toggle:hover {
    transform: translateY(-1px);
    border-color: rgba(36, 70, 216, 0.34);
    box-shadow: 0 10px 20px rgba(30, 44, 86, 0.12);
  }

  .menu-web {
    display: flex;
  }

  .menu-mobile {
    display: none;
  }

  @media (max-width: 776px) {
    height: 74px;
    padding: 0 16px;

    .logo {
      margin: 0 auto;
    }

    .menu-web {
      display: none;
    }

    .menu-mobile {
      display: flex;
    }

    .nav-actions {
      gap: 0.35rem;
    }

    .theme-toggle {
      min-width: 70px;
      height: 34px;
      font-size: 0.74rem;
    }
  }
`;

const Navbar = ({ theme = 'light', onToggleTheme = () => {} }) => {
  return (
    <NavMobile>
      <div className="menu-mobile">
        <Burger />
      </div>
      <div className="logo">
        <a href="/"><img src="images/me.png" alt="bitmoji"/></a>
      </div>
      <div className="nav-actions">
        <div className="menu-web">
          <Burger />
        </div>
        <button className="theme-toggle" onClick={onToggleTheme} aria-label="Toggle color theme">
          {theme === 'dark' ? 'Light' : 'Dark'}
        </button>
      </div>
    </NavMobile>
  );
};

export default Navbar;
