import React from 'react';
import styled from 'styled-components';

const Ul = styled.ul`
  position: static;
  margin-top: 0px;
  list-style: none;
  display: flex;
  flex-flow: row nowrap;
  z-index: 22;

  :hover li a{
    transform:scale(1.5);
    opacity:0.2;
    filter:blur(5px);
  }

  li {
    padding: 30px 25px;
    transition:0.5s;
  }
  // li:active{
  //   text-decoration: underline;
  //   text-decoration-color: black;
  // }
  li a {
    font-family: 'M PLUS Rounded 1c';
    font-weight: bold;
    font-size: 18px;
    color: white;
    text-decoration: none;
  }
  li a:hover {
    // color: 37392E;
    // text-decoration: underline;
    // text-decoration-color: white;

    transform:scale(2);
    opacity:1;
    filter:blur(0);
    text-decoration:none;
    color:#fff;
  }
  li a:before{
    content:'';
    position:absolute;
    top:0;
    left:0;
    width:6vw;
    height:3vw;
    background:#ff497c;
    transition:0.5s;
    transform-origin:right;
    transform:scaleX(0);
    z-index:-1;
  }
  li a:hover:before{
    transition:transform 0.5s;
    transform-origin:left;
    transform:scaleX(1);
  }

  // li a:active {
  //   text-decoration: underline;
  //   text-decoration-color: white;
  // }
  @media (max-width: 776px) {
    margin-top: 90px;
    flex-flow: column nowrap;
    background-color: black;
    position: fixed;
    transform: ${({ open }) => open ? 'translateY(0)' : 'translateY(-150%)'};
    top: 0;
    right: 0;
    height: 40vh;
    width: 100vw; 
    padding-top: 2rem;
    transition: transform 0.5s ease-in-out;
    li {
      color: white;
    }
  }
`;

const RightNav = ({ open }) => {
  return (
    <Ul open={open}>
      <li><a href="/work">Work</a></li>
      <li><a href="/RaunakAnand_Resume.pdf" target="_blank" rel="noreferrer">Resume</a></li>
    </Ul>
  )
}

export default RightNav;
